import { Component, OnInit } from '@angular/core';

interface Member {
  name: string;
  avatar: string;
  role: string;
  isOnShift: boolean;
}

@Component({
  selector: 'app-community',
  templateUrl: './community.page.html',
  styleUrls: ['./community.page.scss'],
})
export class CommunityPage implements OnInit {
  members: Member[] = [];
  filteredMembers: Member[] = [];
  groups: any;

  constructor() {}

  ngOnInit() {
    // Generate some fake data
    this.members = this.generateFakeMembers();
    this.filteredMembers = this.members;

    // Group members by the first letter of their name
    this.groups = this.groupMembersByLetter();
  }

  filterMembers(event: any) {
    const searchTerm = event.target.value.toLowerCase();
    this.filteredMembers = this.members.filter((member) =>
      member.name.toLowerCase().includes(searchTerm)
    );
  }

  // Function to generate fake members data
  generateFakeMembers(): Member[] {
    const fakeMembers: Member[] = [];

    for (let i = 0; i < 100; i++) {
      const fakeMember: Member = {
        name: `User ${i}`,
        avatar: 'path/to/avatar.png',
        role: 'Member', // You can adjust roles as needed
        isOnShift: i % 2 === 0, // Assigns 'true' to every even index
      };

      fakeMembers.push(fakeMember);
    }

    return fakeMembers;
  }

  // Function to group members by the first letter of their name
  groupMembersByLetter() {
    const groups: any[] = [];

    this.filteredMembers.forEach((member) => {
      const firstLetter = member.name.charAt(0).toUpperCase();

      let group = groups.find((g) => g.letter === firstLetter);

      if (!group) {
        group = { letter: firstLetter, members: [] };
        groups.push(group);
      }

      group.members.push(member);
    });

    return groups;
  }

  openSideMenu(event: any) {}

  filterByLetter(event: any) {}

  alphabets = [
    'A', 'B', 'C', 'D', 'E', 'F', 'G', 'H', 'I', 'K', 'L', 'M',
    'N', 'O', 'P', 'Q', 'R', 'S', 'T', 'U', 'V', 'W', 'X', 'Y', 'Z'
  ];

  scrollToLetter(letter: string) {
    const element = document.getElementById(`group-${letter}`);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  }
}
<!-- <ion-header>
  <ion-toolbar>
    <ion-menu-button icon="menu-outline"></ion-menu-button> 
    <ion-title>Community</ion-title>
  </ion-toolbar>
</ion-header> -->

<ion-content class = "background">
  <ion-searchbar (ionInput)="filterMembers($event)"></ion-searchbar>

  <ion-col side="start" class="alphabet-column">
    <!-- Alphabet Navigation -->
    <ion-list>
      <ion-item *ngFor="let alphabet of alphabets">
        <ion-label (click)="scrollToLetter(alphabet)">{{ alphabet }}</ion-label>
      </ion-item>
    </ion-list>
  </ion-col>

  <ion-row class="member-list">
    <ion-col *ngFor="let group of groups" size="4.5">
      <ion-card *ngFor="let member of group.members">
        <ion-avatar>
          <img [src]="member.avatar" />
        </ion-avatar>
        <ion-card-content class="member-info">
          <ion-label class="member-name">{{ member.name }}</ion-label>
          <span class="member-role">{{ member.role }}</span>
          <ion-button size="small" outline [disabled]="!member.isOnShift" color="success">Online</ion-button>
        </ion-card-content>
      </ion-card>
    </ion-col>
  </ion-row>
  
</ion-content>
