import { Component, OnInit } from '@angular/core';
import { AlertController } from '@ionic/angular';

@Component({
  selector: 'app-report',
  templateUrl: './report.page.html',
  styleUrls: ['./report.page.scss'],
})
export class ReportPage implements OnInit {
  isButtonClicked = false;
  notifyingStatement: string = '';
  firstH4Content: string = 'Press the button below';
  secondH4Content: string = 'to notify medics';

  constructor(public alertController: AlertController) {}

  ngOnInit() {}

  async presentAlert() {
    if (this.isButtonClicked) {
      return;
    }

    const alert = await this.alertController.create({
      header: 'Emergency Alert',
      subHeader: 'Please provide emergency information',
      cssClass: "input-alert-dialog",

      inputs: [
        {
          name: 'Location',
          type: 'text',
          placeholder: 'Location...',
          cssClass: 'location-input',
        },

        {
          name: 'Description',
          type: 'text',
          placeholder: 'Description...',
          cssClass: 'description-input'
        },
      ],

      buttons: [
        {
          text: 'Cancel',
          role: 'cancel',
          cssClass: 'alert-button-cancel',
          handler: () => {
            console.log('Cancelled');
          },
        },
        {
          text: 'Send',
          cssClass: 'alert-button-send',
          handler: () => {
            this.sendSOS();
            this.notifyingStatement = 'Notifying medics...';
            this.firstH4Content = "We will let you know";
            this.secondH4Content = "when a medic responds";
          },
        },
      ],
    });
    await alert.present();
  }

  sendSOS() {
    this.isButtonClicked = true;
    const sosButton = document.querySelector('.sos-button');
    if (sosButton) {
      sosButton.classList.add('clicked');
      setTimeout(() => {
        sosButton.classList.remove('clicked');
      }, 500);
    }

    setTimeout(() => {
      this.isButtonClicked = false;
      this.notifyingStatement = 'No medics responded!';
      this.firstH4Content = "Dial #3934 from a nearby";
      this.secondH4Content = "campus phone or try again!";
    }, 60000);
  }
}
<ion-content class="gradient-background">
  <div class="welcome">
    <h3>Welcome,</h3>
    <h5>Joe Wehbe</h5>
  </div>

  <div class="body">
    <div class="stmt">
      <h1>Spotted an emergency?</h1>
      <h4>{{ firstH4Content }}</h4>
      <h4 class="second">{{ secondH4Content }}</h4>
    </div>
  
    <div class="circles-container">
      <button class="sos-button" [ngClass]="{'sos-button-animated': isButtonClicked}" (click)="presentAlert()"><h1>SOS</h1></button>
      <div [ngClass]="{'circle circle-2-animated': isButtonClicked, 'circle circle-2': !isButtonClicked}"></div>
      <div [ngClass]="{'circle circle-3-animated': isButtonClicked, 'circle circle-3': !isButtonClicked}"></div>
    </div>
  
    <div *ngIf="notifyingStatement" class="notif-stmt">
      <h2>{{ notifyingStatement }}</h2>
    </div>
  </div>

</ion-content>
