import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
@Component({
  selector: 'app-no-announcements',
  templateUrl: './no-announcements.page.html',
  styleUrls: ['./no-announcements.page.scss'],
})
export class NoAnnouncementsPage implements OnInit {

  constructor(private router:Router) { }

  ngOnInit() {
  }

  goBack(){
    this.router.navigate(['/folder/Admin Panel']);
  }



}
<ion-header [translucent]="true">
  <ion-toolbar >
    <ion-buttons slot="start">
      <ion-button (click)="goBack()" class="custom-button" expand="block"><ion-icon name="chevron-back-outline" slot="start"></ion-icon></ion-button>
    </ion-buttons>
    <ion-title>Your Announcements</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true" class="gradient-background">
  <ion-header>
    <ion-toolbar class="header-background">
      <ion-title></ion-title>
    </ion-toolbar>
  </ion-header>

  <div id="container">
    <strong class="capitalize">Patient Assessment</strong>
    <p>Nothing to show here! Medics have not yet added any FAQ</p>
    <div class="centered-container">

      <img alt="No Requests" src="assets/imgs/Medical consultation.png" />

    </div>
  </div>




</ion-content>
