import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
@Component({
  selector: 'app-add-extension',
  templateUrl: './add-extension.page.html',
  styleUrls: ['./add-extension.page.scss'],
})
export class AddExtensionPage implements OnInit {

  constructor(private router:Router) { }

  ngOnInit() {
  }

  goBack(){
    this.router.navigate(['manage-extensions']);
  }

}
<ion-header [translucent]="true">
  <ion-toolbar >
    <ion-buttons slot="start">
      <ion-button  (click)="goBack()" class="custom-button" expand="block"><ion-icon name="chevron-back-outline" slot="start"></ion-icon></ion-button>
    </ion-buttons>
    <ion-title>Add Extension</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true" class="gradient-background">

  <div id="container">
    <strong class="capitalize">Add Useful Extensions</strong>
    <p>This allows quick access to extensions during emergencies</p>

    <ion-card>

      <ion-card-content>
        <ion-item class="custom-item">
          <ion-input label="Write the name of the extension" labelPlacement="stacked" placeholder="Name..."  class="custom-input"></ion-input>
        </ion-item>

        <ion-item class="custom-item">
          <ion-input label="Write the number of the extension" labelPlacement="stacked" placeholder="Number..."  class="custom-input"></ion-input>
        </ion-item>


      </ion-card-content>

    </ion-card>

  <ion-button class="add" shape="round">Add</ion-button>
  </div>




 </ion-content>
